const express = require('express')
const knex = require('../../conf/knex.config')
const commonRoutes = express.Router();
const secureMiddlewares = require("../../middlewares/secureMiddlewares");

commonRoutes.get("/test-endpoint", secureMiddlewares.secureAuth, async (req, res, next) => {
    try {
        const cheeseObj = {
            cheese: "parmejano",
            id: 23
        }
        res.send(cheeseObj)
    } catch (e) {
        next(e)
    }
})

commonRoutes.post("/test-endpoint", async (req, res, next) => {
    try {
        console.log(req.body)
    } catch (e) {
        next(e)
    }
})

module.exports = commonRoutes