module.exports = class CustomErrors extends Error {
    status;
    errors;

    constructor(status, message, errors = []) {
        super(message);
        this.status = status;
        this.errors = errors
    }

    static UnauthorizedError() {
        return new CustomErrors(401, 'Пользователь не авторизован')
    }

    static ValidatingError() {
        return new CustomErrors(400, 'Неправильный пароль')
    }

    static BadRequest(message, errors = []) {
        return new CustomErrors(400, message, errors)
    }
}