const initialState = {
    curUser: {},
    isAuth: false,
    isLogin: false
}

const MainReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'USER_FETCHED':
            return {
                ...state,
                curUser: action.payload
            }

        case 'SET_AUTH':
            return {
                ...state,
                isAuth: action.payload
            }
        case 'SET_FORM_TYPE':
            return {
                ...state,
                isLogin: action.payload
            }
        default: return state
    }
}

export default MainReducer;